template.value:
getMetaData: function (language) {
        var result = metadata[language];
        if (result === undefined)
            result = metadata["en"];
        result.description = result.description.replace("{targetTitle}", avatarData.targetTitle);
        return result;
 }

getMapData: function (eventData, locationFacade) {
    var mapData = {list: []};

    var list = locationFacade.getInventoryAddress(avatarId, settings.itemAssetKey);

    for (var i = 0; i < list.length; i++) {
        var address = JSON.parse(list[i]);
        address.state = QuestState.InProgress;
        mapData.list.push(address);
    }

    return mapData;

},
