template.value:
getMapData: function () {
    var mapData = {list: []};

    var tmpList = api.country().myFurnitureList()
        .getAddress(QuestState.Info);

    mapData.list = JSON.parse(tmpList);

    return mapData;
  }

getMetaData: function (language) {
    var data = metadata[language];

    var result = api.metadata().title(data.title)
        .description(data.description)
        .addStep(data.step0, avatarData.steps[0]).get();

    return result;
  }
